CREATE TABLE `tbl_file_info` (`id` int(11) NOT NULL AUTO_INCREMENT, `storage_key` varchar(256) COLLATE utf8mb4_bin NOT NULL, `file_id` varchar(256) COLLATE utf8mb4_bin NOT NULL, `file_url` varchar(512) COLLATE utf8mb4_bin NOT NULL, `version` int(11) DEFAULT '0', `create_time` datetime DEFAULT CURRENT_TIMESTAMP, `update_time` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP, PRIMARY KEY (`id`), UNIQUE KEY `idx_file_id` (`file_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_bin;
